import { HYDRATE } from 'next-redux-wrapper';

const initialState = {
  user: {
    isLogin: false,
    user: null,
    signUpData: {},
    loginData: {},
  },
  post: {
    mainPosts: [],
  }
}

export const loginAction = (data) => {
  return {
    type: 'LOGIN',
    data
  }
}

export const logoutAction = () => {
  return {
    type: 'LOGOUT',
  }
}

// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
  switch(action.type) {
    case 'LOGIN':
      return {
        ...state,
        user: {
          ...state.user,
          isLogin: true,
          user: action.data,
        }
      }
    case 'LOGOUT':
      return {
        ...state,
        user: {
          ...state.user,
          isLogin: false,
          user: null,
        }
      }
  }
}

export default rootReducer;
